mapper.select:
select
id, model_id, source_name, source_input_type, source_code
from bonus_input_source


        and id = #{id}


        and model_id = #{modelId}


        and source_name = #{sourceName}


        and source_input_type = #{sourceInputType}


        and source_code = #{sourceCode}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from bonus_input_source


        and id = #{id}


        and model_id = #{modelId}


        and source_name = #{sourceName}


        and source_input_type = #{sourceInputType}


        and source_code = #{sourceCode}
