mapper.select:
select
  id, indi_id, indi_calculate_str, min_ladder, max_ladder, max_ladder_calculate_str, calculate_source
from bonus_ladder


        and id = #{id}


        and indi_id = #{indiId}


        and indi_calculate_str = #{indiCalculateStr}


        and min_ladder = #{minLadder}


        and max_ladder = #{maxLadder}


        and max_ladder_calculate_str = #{maxLadderCalculateStr}


        and calculate_source = #{calculateSource}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from bonus_ladder


        and id = #{id}


        and indi_id = #{indiId}


        and indi_calculate_str = #{indiCalculateStr}


        and min_ladder = #{minLadder}


        and max_ladder = #{maxLadder}


        and max_ladder_calculate_str = #{maxLadderCalculateStr}


        and calculate_source = #{calculateSource}
